var MenuOpen={init:function(){MenuOpen.thinWidth()},thinWidth:function(){var e=$(".top");if(!e.hasClass("hidden")){e.addClass("hidden")}var t=$("#arrow1");var n=$("#arrow2");var r=$("#arrow3");var i=$("#arrow4");t.click(MenuOpen.arrowClick(t));n.click(MenuOpen.arrowClick(n));r.click(MenuOpen.arrowClick(r));i.click(MenuOpen.arrowClick(i));var s=$("#arrow5");var o=$("#arrow6");var u=$("#arrow7");var a=$("#arrow8");s.click(MenuOpen.arrowClick(s));o.click(MenuOpen.arrowClick(o));u.click(MenuOpen.arrowClick(u));a.click(MenuOpen.arrowClick(a))},arrowClick:function(e){var t=e;t.click(function(){var e=t.next().next();if(t.hasClass("grey")){if(t.hasClass("down")){t.addClass("up");t.removeClass("down");t.attr("src","../images/arrowUpGrey.gif");e.addClass("visible");e.removeClass("hidden")}else{t.addClass("down");t.removeClass("up");t.attr("src","../images/arrowDownGrey.gif");e.addClass("hidden");e.removeClass("visible")}}else{if(t.hasClass("down")){t.addClass("up");t.removeClass("down");if($("h1").hasClass("home")){t.attr("src","images/arrowUp.gif")}else{t.attr("src","../images/arrowUp.gif")}e.addClass("visible");e.removeClass("hidden")}else{t.addClass("down");t.removeClass("up");if($("h1").hasClass("home")){t.attr("src","images/arrowDown.gif")}else{t.attr("src","../images/arrowDown.gif")}e=t.next().next();e.addClass("hidden");e.removeClass("visible")}}})}};MenuOpen.init()